import React, {useState, Component } from 'react';
import EmployeeServices from '../services/EmployeeServices';
import { Modal, Button, Row, Col, Form } from 'react-bootstrap';

class ListEmployeeComponents extends Component {
    constructor(props) {
        super(props)

        const [show, setShow] = useState(false);

        const handleClose = () => setShow(false);
        const handleShow = () => setShow(true);
       
        this.state = {
            cusfullname: '',
            id: this.props.match.params.id,
            customers: []
        }
        this.editCustomer = this.editCustomer.bind(this);
        this.getDataById = this.getDataById.bind(this);
    }
    componentDidMount() {
        EmployeeServices.getCustomers().then((res) => {
            this.setState({ customers: res.data });
        });
    }
    editCustomer(id) {

        this.props.history.push(`/update-customer/${id}`);
    }
    getDataById(id) {
       
        // console.log(id);
        EmployeeServices.getEmployeeById(id).then((res) => {
            let customer = res.data;

            this.setState({
                cusfullname: customer.cusfullname,
            });
            // console.log(customer);
        });
        // EmployeeServices.getEmployeeById().then((res)=>{
        //     let customer=res.data;
        //     console.log(customer);
        // });
       
    }
    render() {
       
        return (
            <div>
                <div>
                    <div className="content-wrapper">
                        {/* Content Header (Page header) */}
                        <div className="content-header">
                            <div className="container-fluid">
                                <div className="row mb-2">
                                    <div className="col-sm-6">
                                        <h4>Employye list</h4>
                                    </div>{/* /.col */}
                                    <div className="col-sm-6">
                                        <ol className="breadcrumb float-sm-right">
                                            <li className="breadcrumb-item"><a href="#">Home</a></li>
                                            <li className="breadcrumb-item active">Dashboard v2</li>
                                        </ol>


                                    </div>{/* /.col */}
                                </div>{/* /.row */}
                            </div>{/* /.container-fluid */}
                        </div>

                        <section className="content">
                            <div className="container-fluid">
                                {/* Info boxes */}


                                <div className="row">
                                    <div className="col-12">
                                        <div className="card">
                                            <div className="card-header">
                                                <h3 className="card-title">DataTable with minimal features & hover style</h3>
                                            </div>

                                            <div className="card-body">
                                                <table id="example2" className="table table-striped table-hover">
                                                    <thead>
                                                        <tr>
                                                            <th>NIC Number</th>
                                                            <th>Full Name</th>
                                                            <th>Date of birth</th>
                                                            <th>Email address</th>
                                                            <th>Actions</th>
                                                        </tr>
                                                    </thead>
                                                    <tbody>
                                                        {
                                                            this.state.customers.map(
                                                                customer => <tr key={customer.cus_id}>
                                                                    <td>{customer.cusNicNumber}</td>
                                                                    <td>{customer.cusfullname}</td>
                                                                    <td>{customer.cusdob}</td>
                                                                    <td>{customer.cusemail}</td>
                                                                    <td>
                                                                        <button onClick={() => this.getDataById(customer.id)} className="btn btn-info btn-sm" style={{ marginRight: 10 }}><i class="far fa-file"></i></button>
                                                                        <button onClick={() => this.editCustomer(customer.id)} className="btn btn-warning btn-sm" style={{ marginRight: 10 }}><i class="fas fa-pen-square"></i></button>
                                                                        <button onClick={() => this.editEmployee(customer.id)} className="btn btn-danger btn-sm"><i class="fas fa-trash-alt"></i></button>
                                                                    </td>
                                                                </tr>
                                                            )
                                                        }
                                                    </tbody>
                                                    {/* <tfoot>
                                                        <tr>
                                                            <th>Rendering engine</th>
                                                            <th>Browser</th>
                                                            <th>Platform(s)</th>
                                                            <th>Engine version</th>
                                                            <th>CSS grade</th>
                                                        </tr>
                                                    </tfoot> */}
                                                </table>
                                            </div>
                                            {/* <!-- /.card-body --> */}
                                        </div>
                                        {/* <!-- /.card --> */}

                                    </div>

                                </div>



                            </div>
                        </section>
                        <>
      <Button variant="primary" onClick={handleShow}>
        Launch demo modal
      </Button>

      <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>Woohoo, you're reading this text in a modal!</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleClose}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
    </>
                        <input type="text" className="form-control" id="cusemail" placeholder="Enter email" value={this.state.cusfullname} />
                    </div>

                </div>
            </div>
        );
    }
}

export default ListEmployeeComponents;
