import React, { Component } from 'react';

class AddEmployeeComponents extends Component {
    constructor(props) {
        super(props)
        this.state = {
            cus_full_name: '',
            cus_nic_number: '',
            cusdob: '',
            cus_contact_number: '',
            cus_email: ''


        }
        this.changeNameHandlter = this.changeNameHandlter.bind(this);
        this.changeNICHandlter = this.changeNICHandlter.bind(this);
        this.changeDateHandlter = this.changeDateHandlter.bind(this);
        this.changeContactHandlter = this.changeContactHandlter.bind(this);
        this.changeEmailtHandlter = this.changeEmailtHandlter.bind(this);
        this.saveCustomer = this.saveCustomer.bind(this);

    }
    saveCustomer = (e) => {
        e.preventDefault();
        let customer = { cus_full_name: this.state.cus_full_name, cus_nic_number: this.state.cus_nic_number, cusdob: this.state.cusdob, cus_contact_number: this.state.cus_contact_number, cus_email: this.state.cus_email }
    }
    changeNameHandlter = (event) => {
        this.setState({
            cus_full_name: event.target.value
        });
    }
    changeNICHandlter = (event) => {
        this.setState({
            cus_nic_number: event.target.value
        });
    }
    changeDateHandlter = (event) => {
        this.setState({
            cusdob: event.target.value
        });
    }
    changeContactHandlter = (event) => {
        this.setState({
            cus_contact_number: event.target.value
        });
    }
    changeEmailtHandlter = (event) => {
        this.setState({
            cus_email: event.target.value
        });
    }
    render() {
        return (
            <div>
                <div>
                    <div className="content-wrapper">
                        {/* Content Header (Page header) */}
                        <div className="content-header">
                            <div className="container-fluid">
                                <div className="row mb-2">
                                    <div className="col-sm-6">
                                        <h4>Employye list</h4>
                                    </div>{/* /.col */}
                                    <div className="col-sm-6">
                                        <ol className="breadcrumb float-sm-right">
                                            <li className="breadcrumb-item"><a href="#">Home</a></li>
                                            <li className="breadcrumb-item active">Dashboard v2</li>
                                        </ol>


                                    </div>{/* /.col */}
                                </div>{/* /.row */}
                            </div>{/* /.container-fluid */}
                        </div>

                        <section className="content">
                            <form role="form">
                                <div className="container-fluid">
                                    {/* Info boxes */}





                                    <div className="row">
                                        {/* left column */}
                                        <div className="col-md-6">
                                            {/* general form elements */}
                                            <div className="card card-default">
                                                <div className="card-header">
                                                    <h3 className="card-title">Quick Example</h3>
                                                </div>
                                                {/* /.card-header */}
                                                {/* form start */}

                                                <div className="card-body">
                                                    <div className="form-group">
                                                        <label htmlFor="exampleInputEmail1">Customer Name</label>
                                                        <input type="text" className="form-control" id="cus_full_name" placeholder="Enter Full name" value={this.state.cus_full_name} onChange={this.changeNameHandlter} />
                                                    </div>
                                                    <div className="form-group">
                                                        <label htmlFor="exampleInputEmail1">NIC Number</label>
                                                        <input type="text" className="form-control" id="cus_nic_number" placeholder="Enter NIC number" value={this.state.cus_nic_number} onChange={this.changeNICHandlter} />
                                                    </div>
                                                    <div className="form-group">
                                                        <label htmlFor="exampleInputEmail1">Date of birth</label>
                                                        <input type="date" className="form-control" id="cusdob" value={this.state.cusdob} onChange={this.changeDateHandlter} />
                                                    </div>



                                                </div>
                                                {/* /.card-body */}


                                            </div>


                                        </div>
                                        {/*/.col (left) */}
                                        {/* right column */}
                                        <div className="col-md-6">
                                            {/* general form elements disabled */}
                                            <div className="card card-default">
                                                <div className="card-header">
                                                    <h3 className="card-title">General Elements</h3>
                                                </div>
                                                {/* /.card-header */}
                                                <div className="card-body">

                                                    <div className="form-group">
                                                        <label htmlFor="exampleInputEmail1">Contact number</label>
                                                        <input type="number" className="form-control" id="cus_contact_number" placeholder="Enter Contact number" value={this.state.cus_contact_number} onChange={this.changeContactHandlter} />
                                                    </div>
                                                    <div className="form-group">
                                                        <label htmlFor="exampleInputEmail1">Email</label>
                                                        <input type="text" className="form-control" id="cus_email" placeholder="Enter email" value={this.state.cus_email} onChange={this.changeEmailtHandlter} />
                                                    </div>






                                                </div>
                                                {/* /.card-body */}
                                            </div>
                                            {/* /.card */}
                                            {/* general form elements disabled */}

                                        </div>
                                        {/*/.col (right) */}
                                    </div>

                                    {/* <div className="card-footer"> */}
                                    <button type="submit" className="btn btn-primary" onClick={this.saveCustomer}>Submit</button>

                                    {/* </div> */}







                                </div>
                            </form>
                        </section>
                    </div>

                </div>
            </div>
        );
    }
}

export default AddEmployeeComponents;
